body {
    margin:0;
    padding:0;
    background-color: #141414;
}

header {
    display:flex;
    flex-direction: row;
    align-items: center;
    & .header-logo {
    display:flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-start;
    padding:20px;
    & >h3 {
        padding:20px;
    }
    }
    & .header-buttons {
        display:flex;
        flex-direction: row;
        flex-grow:2;
        justify-content: flex-end;
        & h3 {
            padding-right:40px; 
        }
    }
    & h3 {
        color:white;
        font-weight: 300;
    }
}

.section-title {
    display:flex;
    flex-direction: row;
    & h3 {
        color:white;
        font-weight: 300;
    }
    & #top-text-tablet {
        display:none;
    }
}

.content {

    .top {
        width:100%;
        display:grid;
        grid-template-columns:repeat(5, 1fr);
        .top-item img{
                max-width:100%;
            }
    }

    .first {
        display:grid;
        grid-template-columns:repeat(6, 1fr);
        .item {
            transition: all .2s ease-in-out;

            img{
                max-width:100%;

            }
           }
        .item:hover {
            transform: scale(1.2);
            flex-grow: 2;
        }
    }

    .second {
        display:grid;
        grid-template-columns:repeat(6, 1fr);
        .item {
            display:flex;
            img{
                max-width:100%;
                transition: all .2s ease-in-out;
            }
            img:hover {
                transform: scale(1.2);
                flex-grow: 2;
            }
        }

    }
    @media only screen and (max-width: 1280px)  {

        header .header-logo > h3 {
            display:none;
            color:red!important;
            font-size:50px;
        }
        .section-title {
             #top-text-tablet {
                display:inline-block!important;
                font-size:50px;
            }
            #top-text {
                display:none!important;
            }
        }
